#}



if( ($EmpId eq "$Emp_Id") && ($EmpPW eq "$Emp_PW"))
{
        if ($EmpId eq =~/\b1/){
             print "Content-type: text/html\n\n";
             print "Good day ^ ^ $EmpFname $EmpLname\n";
             print "right password.";
         # print "<p><a href='viewpro.pl'>Log-in info.</a></p>";
             print "<p><a href='viewHresource.pl'>Human Resource</a></p>";
             print "<p><a href='viewpro.pl'>Sales Part</a></p>";
     } else if ($EmpId eq =~/\b2/){
             print "Content-type: text/html\n\n";
             print "Good day ^ ^ $EmpFname $EmpLname\n";
             print "right password.";
         # print "<p><a href='viewpro.pl'>View Employees</a></p>";
             print "<p><a href='viewHresource.pl'>View Employees</a></p>";
         print "<p><a href='hresource1.pl'>Add Employee</a></p>";
        } else if ($EmpId eq =~/\b3/){
             print "Content-type: text/html\n\n";
             print "Good day ^ ^ $EmpFname $EmpLname\n";
             print "right password.";
         print "<p><a href='viewpro.pl'>Sales Part</a></p>";
             print "<p><a href='Addpro1.pl'>Add Product</a></p>";
         # print "<p><a href='viewHresource.pl'>View Employees</a></p>";
         # print "<p><a href='hresource1.pl'>Add Employee</a></p>";
        }else
             {
                 print "Content-type: text/html\n\n";
                 print "wrong password.";
             print "<a href=\"javascript:history.go(-1)\">? Back</a>\n";
             }
             }

else
{
print "Content-type: text/html\n\n";
print "wrong password.";
print "<a href=\"javascript:history.go(-1)\">? Back</a>\n";
}

$rs->Close();
$Conn->Close();

