sub print_list{
	if($CONF{'skin'} ne ""){
		require "./skin/$CONF{'skin'}";
		&main_face;
	}
	else{
		my($d, $color_val, $up_td, $bsk_td, $io_td, $zero_col, $search_option, $view_option);
		local($down_col, $pnt_col, $pre_pg, $next_pg);
		$down_col="<td align=center><font color=$CONF{'hfont_color'}>다운</font></td>" if($CONF{'down_view'} eq "yes");
		$pnt_col="<td width=27 align=center><font color=$CONF{'hfont_color'}>추천</font></td>" if($CONF{'point_ok'} eq "yes");

	#	$scount = (($page-1)*$CONF{'page_num'});
	#	$icount = 0;
		$d = 0; 
		$pre_pg= $page - 1;
		$next_pg = $page + 1;
		&Fhead("POST","$cgi_url");
		print &Input("hidden","id","$FORM{'id'}");
		print &Input("hidden","action","multi_view");
		print &Input("hidden","page","$page");
		if($act eq "search"){
			print &Input("hidden","category","$FORM{'category'}");
			print &Input("hidden","keyword","$FORM{'keyword'}");
			print &Input("hidden","start_num","$FORM{'start_num'}");
		}
		&top_btn;
		&Thead("100%","","0","0");
		$color_val = $CONF{'head_color'} ? "bgcolor=$CONF{'head_color'}": "";
		print "<tr><td $color_val height=2><img src=$img_url/zero.gif width=0 height=2 border=0></td></tr>\n";
		&Tfoot;
		$up_td = ($CONF{'kind_view'} eq "yes")? "<td>&nbsp;</td> " : "";
		$bsk_td = "<td width=22 align=center><input type=image name=mv value=ok src=$img_url/bsk.gif border=0 width=15 height=13 alt=\"여러글 한꺼번에 보기\"></td>";
		$io_td = ($CONF{'i_opt'} eq "yes") ? "<td width=34 align=center><font color=$CONF{'hfont_color'}><b>ⓘ</b></td>" : "";
		if($page eq $total_page){
			print "<input type=hidden name=total_val value=ok>";
		}
		&Thead("100%","","1","0");
		print "<tr $color_val align=center>$bsk_td<td align=left><font color=$CONF{'hfont_color'}><b>ⓝ</b></font></td>$up_td<td align=center><font color=$CONF{'hfont_color'}>글 제 목</font></td><td align=center><font color=$CONF{'hfont_color'}>작성자</font></td>$io_td<td align=center width=47><font color=$CONF{'hfont_color'}>작성일</font></td><td align=center><font color=$CONF{'hfont_color'}>조회</font></td>$down_col $pnt_col</tr>\n";
		&read_notice if(($page eq "1") && ($FORM{'category'} eq ""));
		if($cou eq "0" || (($page eq "1") && ($ary[0] eq ""))){
			$zero_col = "7";
			if($CONF{'down_view'} eq "yes"){
				$zero_col++;
			}				
			if($CONF{'point_ok'} eq "yes"){
				$zero_col++;
			}
			if($CONF{'i_opt'} eq "yes"){
				$zero_col++;
			}
			my($no_msg) = ("$FORM{'category'}") ? "^.^ 찾으시는 글이 없어요!" : "^.^ 등록된 글이 없습니다. ^.^";
			my($over_msg) = &over("$no_msg");
			my($color_val) = ($CONF{'cel_color1'}) ? "bgcolor=$CONF{'cel_color1'}" : "";
			print "<tr $color_val $over_msg><td align=center colspan=$zero_col><font size=-1 color=red>$no_msg</font></td></tr>\n";
		}
		else{
			my($start, $end, $no_data);
			$start = $CONF{'page_num'}*($page - 1);
			$end = $start + $CONF{'page_num'} - 1;
			$no_data = $list_num - (($page-1)*$CONF{'page_num'});

			my($chk_num) = 0;
			my(@gubun, @open, $open_val, $eval, $num, $read_num, $down_num, $man_cnt, $pnt_val, $e_val, $pnt_gif, $pnt_img);
			local(@down_data, $file_name, $file_size, $type);
			for($start..$end) {
				last if($ary[$_] eq "");
				$num = $ary[$_];
				#$icount++;
				#if($icount > $scount) {
					open(FILE,"$main_dir/data/$num");
					local @data = <FILE>;
					close (FILE);
					&file_type;

					open(NUM, "$main_dir/num/$num");
					$num_data = <NUM>;
					close(NUM);
					@gubun = split(/\|/,"$num_data");
					my($read_num) = $gubun[1];
					my($down_num) = $gubun[2];
					if($CONF{'point_ok'} eq "yes"){
						$man_cnt = $gubun[3] ? "$gubun[3]" : "0";
						$pnt_val = $gubun[4] ? "$gubun[4]" : "0";
						$e_val = $man_cnt ? ($pnt_val/$man_cnt) : "0"; 
						$e_val = ($e_val eq "100")? 100 : substr($e_val,0,2);
						if(($e_val > 90) && ($e_val <= 100)){
							$pnt_gif = "100.gif";
						}
						elsif(($e_val > 70) && ($e_val <= 90)){
							$pnt_gif = "80.gif";					
						}
						elsif(($e_val > 50) && ($e_val <= 70)){
							$pnt_gif = "60.gif";			
						}
						elsif(($e_val > 30) && ($e_val <= 50)){
							$pnt_gif = "40.gif";					
						}
						elsif(($e_val > 10) && ($e_val <= 30)){
							$pnt_gif = "20.gif";			
						}
						else{
							$pnt_gif = "0.gif";	
						}
						$pnt_gif = "gray/$pnt_gif" if($man_cnt < $CONF{'pnt_cnt'}); 
						$pnt_img = "<img src=$face_url/$pnt_gif alt=\"추천인수 [$man_cnt명] 평균점수 [$e_val점] 총점수[$pnt_val점]\" width=16 height=16>";	

					}
					$read_num = $read_num ? "$read_num" : "0";
		#------------------------------------------------------------------------------
					my(@push, $rotate, $w_name, $home_img, $rdate, $gul_icon, $subject, $x_num, $gul_sub, $kword, $mailto, $mail_img, $read_val);
					local(@list, $tr_color);
					$d++;
					if($CONF{'templet'} ne ""){
						$tr_color = $gr_color[$d-1];
					}
					else{
						$rotate = $d%2;
						$tr_color = ($rotate eq "0") ? "$CONF{'cel_color2'}" : "$CONF{'cel_color1'}";
					}

					@list = split(/\|/, $data[1]);
					@open = split(/\|/, $data[4]);
					$open_val = $open[2];
					if(($down_data[5] eq "") && ($down_data[4] eq "")) {
						$file_icon = "<img src=$type_url/text.gif width=16 height=16 border=0 alt=\"옆에 동그란 아이콘이나 화살표를 클릭하시면 작은창보기가 됩니다.\"> ";
						$down_num = "*";
						$img_ok = "no";
					}
					else{
						$type = &file_icon("$type");
						$img_ok = (($type eq "gif.gif") || ($type eq "jpg.gif") || ($type eq "jpeg.gif")) ? "ok" : "no";
						$file_icon = ($open_val ne "no") ? "<a href=\"$cgi_url?id=$FORM{'id'}&number=$num&file=$f_name&action=down_file\"><img src=$type_url/$type width=16 height=16 border=0 alt=\"$file_name($file_size) 다운받기\"></a> " : "<img src=$type_url/$type alt=\"이파일은 비공개 파일입니다...\" border=0 width=16 height=16>";
						$down_num = $down_num ? "$down_num" : "0";
					}
					$w_name = &cut_len("$list[0]","$CONF{'name_length'}");
					$home_img = $list[2] ? "<a href=\"$list[2]\" target=_blank><img src=$img_url/home.gif width=10 height=10 border=0 alt=\"$list[0]님 집에 놀러가기\"></a>": "<img src=$img_url/gray_home.gif width=10 height=10 width=10 height=10 border=0>";
					$rdate=substr($list[4],5,5);
					if(-M "$main_dir/data/$num" < 1.0) {
						$gul_icon = ($num =~/-/) ? "newans.gif" : "newgul.gif";
						$gul_icon = "new_lock.gif" if($open_val eq "no");
					} 
					else{ 
						$gul_icon = ($num =~/-/) ? "ans.gif" : "gul.gif";
						$gul_icon = "lock.gif" if($open_val eq "no");
					}  

					$subject = "<img src=$img_url/$gul_icon border=0  alt=\"Simple view\">";

					@push = split(/-/,$num);
					shift(@push);
					#---------------------------------------------------[ 제목글 길이 제한 ]
					$x_num = @push;
					$gul_sub = &cut_len("$list[3]", "$CONF{'sub_length'}");
					$x_num *=17;
					#---------------------------------------------------
					#---------------------------------------------------[간단의견 갯수조회]
					if((-e "$main_dir/etc/$num") && ($CONF{'etc_ok'} eq "yes")){
						open(ETC, "$main_dir/etc/$num");	
						my @etc = grep(!/^\s/, <ETC>);
						close(ETC);
						my $etc_num = $#etc+1;
						$gul_sub .= (-M "$main_dir/etc/$num" < 1.0) ? " <font size=1 face=Tahoma color=$CONF{'date_color'}>[$etc_num]</font>" : " <font size=1 face=Tahoma>[$etc_num]</font>" if($etc_num > 0);	
					}
					#---------------------------------------------------
					#---------------------------------------------------[검색시 검색어 구별]
					if($act eq "search"){
						$kword = "$FORM{'keyword'}";
						$gul_sub =~ s/$kword/<B><FONT COLOR=$CONF{'date_color'}>$kword<\/font><\/b>/g if(($FORM{'category'} eq "subject") || ($FORM{'category'} eq "sub_cmt"));
						$w_name =~ s/$kword/<B><FONT COLOR=$CONF{'date_color'}>$kword<\/font><\/b>/g if($FORM{'category'} eq "name");
					}
					#--------------------------------------------------------------------------------

					if($list[1]) { 
						if($NT eq "1"){
							$mailto = "<a href=mailto:$list[1]><font color=$CONF{'font_color'}>$w_name</font></a>";
							$mail_img = "<a href=mailto:$list[1]><img src=$img_url/mail.gif width=14 height=10 border=0 alt=\"$list[0]님에게 편지쓰기\"></a>";
						}
						else{
							$mailto = "<a href=\"javascript:newin(400, 267, 'sendmail.cgi?name=$list[0]&mail=$list[1]&prog=$sendmail&home_url=$CONF{'home_url'}','spmail')\"><font color=$CONF{'font_color'}>$w_name</font></a>";
							$mail_img = "<a href=\"javascript:newin(400, 267, 'sendmail.cgi?name=$list[0]&mail=$list[1]&prog=$sendmail&home_url=$CONF{'home_url'}','spmail')\"><img src=$img_url/mail.gif width=14 height=10 border=0 alt=\"$list[0]님에게 편지쓰기\"></a>";
						}
					}
					else {
						$mailto = "<font color=$CONF{'font_color'}>$w_name</font>";
						$mail_img= "<img src=$img_url/gray_mail.gif width=14 height=10 width=14 height=10 border=0>";
					}   
					my($icon_td, $num_col, $pnt_kind, $m_subject, $mh_td, $chk_td, $over_msg, $color_val);
					$icon_td = ($CONF{'kind_view'} eq "yes") ? "<td width=16 align=center>$file_icon</td>" : "";
					$num_col = "<td align=center><font color=$CONF{'down_color'}><b>$down_num</b></font></td>" if($CONF{'down_view'} eq "yes");
					$pnt_kind = "<td align=center>$pnt_img</td>" if($CONF{'point_ok'} eq "yes");

					if(($num eq $FORM{'number'}) && ($act ne "modify_ok") && ($act ne "multi_view")){
						$m_subject = "$subject <u><b>$gul_sub</b></u>\n";
						$tr_color = $CONF{'select_color'};
					}
					else{
						$search_option = "&category=$FORM{'category'}&keyword=$FORM{'keyword'}&start_num=$FORM{'start_num'}" if($FORM{'category'} ne "");
						$m_subject = "<a href=\"javascript:newin(580, 400, '$cgi_url?id=$FORM{'id'}&page=$page&action=view&number=$num&simple=yes$search_option','simple_view')\"> $subject</a> <a href=$cgi_url?id=$FORM{'id'}&page=$page&action=view&number=$num&img=$img_ok$search_option><font color=$CONF{'font_color'}>$gul_sub</font></a>\n";

						#여러글 보기에서 읽고있는글 선택색으로 되게... ^.^
						if($act eq "multi_view"){
							for(0..$#view_num){
								if($view_num[$_] eq "$num"){
									$tr_color = $CONF{'select_color'};
									last;
								}
							}
						}				
					}

					$mh_td = "<td align=center>$mail_img $home_img</td>" if($CONF{'i_opt'} eq "yes");
					$chk_td = "<td align=center><input type=checkbox name=num_val$chk_num value=$num></td>";
					$over_msg = &over(" ▷▷ $no_data 번 글 읽기 ^.^&nbsp");
					$color_val = ($tr_color) ? "bgcolor=$tr_color" : "";
					print "<tr $color_val $over_msg>$chk_td<td><font color=$CONF{'num_color'}>$no_data</td>$icon_td<td><img src=$img_url/zero.gif height=10 width=$x_num> $m_subject</font></td><td align=center>$mailto</td>$mh_td<td align=center><font color=$CONF{'date_color'}>$rdate</font></td><td align=center><font color=$CONF{'read_color'}>$read_num</font></td>$num_col $pnt_kind</tr>\n";
					$chk_num++;
				#}
				$no_data--;
				#last if($d >= $CONF{'page_num'});
			}
		}

		$zero_col = "6";
		if($CONF{'down_view'} eq "yes"){
			$zero_col++;
		}				
		if($CONF{'point_ok'} eq "yes"){
			$zero_col++;
		}
		if($CONF{'i_opt'} eq "yes"){
			$zero_col++;
		}
		if($CONF{'kind_view'} eq "yes"){
			$zero_col++;
		}

		$zero_col -= 2;
		print "<tr><td align=center width=22 background=$img_url/spot.gif><img src=$img_url/zero.gif></td><td colspan=$zero_col  background=$img_url/spot.gif><img src=$img_url/zero.gif></td><td  background=$img_url/spot.gif><img src=$img_url/zero.gif></td></tr>\n";
		print "<tr><td align=center width=22><input type=image src=$img_url/chk_del.gif border=0 width=15 height=13 alt=\"체크된 항목 삭제\"></td><td colspan=$zero_col>";
	}

	# 스킨 끝... ^^
	&pagemove;
	$search_option .= "&action=search" if($act eq "search");
	$view_option = "&action=view&number=$FORM{'number'}&img=$FORM{'img'}" if($act eq "view");
	print  "</td><td align=right><a href=$cgi_url?id=$FORM{'id'}&page=$page$search_option$view_option><img src=$img_url/zero.gif border=0 width=15 height=13 alt=\"reload\"></td></tr>\n";
	&Tfoot;	
	&Ffoot;
	print "<br>\n";
}
# sub print_list{} 끝 ---------------------------------------------

sub over{
	my($msg) = $_[0];
	return "onMouseOver=\"this.style.backgroundColor='$CONF{'over_color'}';window.status=('$msg');return true;\" onMouseOut=\"this.style.backgroundColor='$tr_color'; window.status=('');return true;\"";
}

sub display {
	if((-e "$main_dir/idx/$page.cgi") && ($FORM{'category'} eq "")){
		open(NUM, "$main_dir/idx/num.cgi");
		$list_num = <NUM>;
		close(NUM);
		if($list_num eq "0" || $list_num eq ""){  #------------------ 글삭제시 게시판에 글이 하나도 남지 않아을 경우...
			&read_idx;
			$list_num = @ary;
			$total_page = int($list_num/$CONF{'page_num'});
			if(($total_page*$CONF{'page_num'}) < $list_num) {$total_page += 1;}
		}
		else{
			$total_page = int($list_num/$CONF{'page_num'});
			if(($total_page*$CONF{'page_num'}) < $list_num) {$total_page += 1;}

			open(PAGE, "$main_dir/idx/$page.cgi");
			@page_list = <PAGE>;
			close(PAGE);
			if($page_list[0] eq "\n"){ #---------- 맨마지막 페이지에서 글 삭제시 그 페이지에 글이 하나도 없을경우...
				$page=$page - 1;
				open(PAGE, "$main_dir/idx/$page.cgi");
				@page_list = <PAGE>;
				close(PAGE);
			}
			$start =($page-1)*$CONF{'page_num'};
			for(1..$CONF{'page_num'}){
				chomp($ary[$start+$_-1] = $page_list[$_-1]);
				if($page_list[$_] eq "\n"){
					last;
				}
			}

			if($page ne "1"){
				$prev = $page - 1;
				open(PREV,"$main_dir/idx/$prev.cgi");
				@prev = <PREV>;	
				close(PREV);
				chomp($ary[$prev*$CONF{'page_num'} - 1] = $prev[$#prev]);
			}
			if($page ne "$total_page"){
				$next = $page + 1;
				open(NEXT,"$main_dir/idx/$next.cgi");
				@next = <NEXT>;
				close(NEXT);
				chomp($ary[$page*$CONF{'page_num'}] = $next[0]);
			}
		}
	}
	else{
	#------------------------------------------- 인덱스 파일 이용
		&read_idx;
		$list_num = @ary;
		$total_page = int($list_num/$CONF{'page_num'});
		if(($total_page*$CONF{'page_num'}) < $list_num) {$total_page += 1; }
		$page = $total_page if("$FORM{'total_val'}");
	}
	#---------------------------------------------------------
	$total_page = 1 if($total_page == 0);

	my($next_num, $prev_num, $prev_page, $next_page, $category, $keyword, $start_num, $simple);

	if(($act eq "view") || ($act eq "etc_write") || ($act eq "etcdel_ok")){
		unless($FORM{'category'} eq ""){
			for(0..$list_num){
				if($FORM{'number'} eq $ary[$_]){
					$next_num = $ary[$_-1];
					$prev_num = $ary[$_+1];
					last;
				}
			}
		}
		else{
			for(($page-1)*$CONF{'page_num'}..$page*$CONF{'page_num'}-1){
				if($FORM{'number'} eq $ary[$_]){
					$next_num = $ary[$_-1];
					$prev_num = $ary[$_+1];
					last;
				}
			}
		}

		$category = $FORM{'category'} ? "&category=$FORM{'category'}" : "";
		$keyword = $FORM{'keyword'} ? "&keyword=$FORM{'keyword'}" : "";
		$start_num = $FORM{'start_num'} ? "&start_num=$FORM{'start_num'}" : "";
		$simple = $FORM{'simple'} ? "&simple=yes" : "";

		$prev_page = (($ary[($page-1)*$CONF{'page_num'}-1] eq "$next_num") && ($category eq "")) ? ($page -1) : "$page" ;
		$next_page = (($ary[$page*$CONF{'page_num'}] eq "$prev_num") && ($category eq "")) ? ($page + 1) : "$page";

		my(@ndata, @nline, @pdata, @pline, $next_data, $prev_data);

		open(NEXT, "$main_dir/data/$next_num");
		@ndata = <NEXT>;
		@nline = split(/\|/,$ndata[1]);
		$nline[3] = &cut_len("$nline[3]","$CONF{'sub_length'}");
		$next_data = " <a href=$cgi_url?id=$FORM{'id'}&page=$prev_page&action=view&number=$next_num$category$keyword$start_num$simple><font color=$CONF{'font_color'}>▲ $nline[3] - $nline[0]</font></a>";
		close (NEXT);

		open(PREV, "$main_dir/data/$prev_num");
		@pdata = <PREV>;
		@pline = split(/\|/,$pdata[1]);
		$pline[3] = &cut_len("$pline[3]","$CONF{'sub_length'}");
		$prev_data = " <a href=$cgi_url?id=$FORM{'id'}&page=$next_page&action=view&number=$prev_num$category$keyword$start_num$simple><font color=$CONF{'font_color'}>▼ $pline[3] - $pline[0]</font></a>";
		close (PREV);

		&Thead("100%","","3","0");
		print "<tr><td>\n";
		print " &nbsp; &nbsp; $next_data<br>" if($FORM{'number'} ne $ary[0]);
		print " &nbsp; &nbsp; $prev_data" if($FORM{'number'} ne $ary[$list_num-1]);
		print "</td></tr>";
		&Tfoot;
		print "<br>\n";
	}
	elsif($act eq "multi_view"){
	}
	else{
		&head;
	}

	if(($FORM{'category'} eq "") && ($FORM{'simple'} ne "yes")){
		if(@quick_id){
			&quick_btn;
		}
		&print_list;
		&find if($CONF{'search_ok'} eq "yes");
	}
	&foot;
}

sub down_file {
	my($t_file);
	&plus_num;
	if(($FORM{'file'}=~ /http:\/\/.+/) || ($FORM{'file'}=~ /ftp:\/\/.+/)){
		print "Location:$FORM{'file'}\n\n";
	}
	else {
		#cgi파일이나 이미지 파일의 다운로드 구현을 위하여...	
		($t_file = $FORM{'file'}) =~ s{^.+/(.+)$}{$1}g;
		print "Content-type: application/octet-stream\n";
		print "Content-disposition: attachment; filename=$t_file\n";
		print "Content-Transfer-Encoding: binary\n\n";

		open( DOWN, "$main_dir/files/$FORM{'file'}" );
		binmode DOWN;
		binmode STDOUT;
		binmode STDERR;
		print <DOWN>;
		close DOWN;
	}	
	exit;
}

sub plus_num{
	my(@num_gubun, $number_data, $ans_number);
	open(NUM, "$main_dir/num/$FORM{'number'}");
	$number_data = <NUM>;
	close(NUM);
	@num_gubun = split(/\|/,"$number_data");
	$ans_number = $num_gubun[0];
	$read_number = $num_gubun[1];
	$down_number = $num_gubun[2];
	$man_cnt = $num_gubun[3] ? "$num_gubun[3]" : "0";
	$pnt_val = $num_gubun[4] ? "$num_gubun[4]" : "0";
	$e_val = $man_cnt ? ($pnt_val/$man_cnt) : "0"; 
	if($e_val > 90 && $e_val <= 100){
		$face_val = "<img src=$face_url/100.gif width=16 height=16 alt=\"우왕~ 좋다!!!\">";
	}
	elsif($e_val > 70 && $e_val <= 90){
		$face_val = "<img src=$face_url/80.gif width=16 height=16 alt=\"음~ 괜찮군...\">";	
	}
	elsif($e_val > 50 && $e_val <= 70){
		$face_val = "<img src=$face_url/60.gif width=16 height=16 alt=\"쫴매 좋네~\">";	
	}
	elsif($e_val > 30 && $e_val <= 50){
		$face_val = "<img src=$face_url/40.gif width=16 height=16 alt=\"음~ 별루다~\">";	
	}
	elsif($e_val > 10 && $e_val <= 30){
		$face_val = "<img src=$face_url/20.gif width=16 height=16 alt=\"이게 뭐야~\">";	
	}
	else{
		$face_val = "<img src=$face_url/0.gif width=16 height=16 alt=\"추천좀 해줘유~\">";	
	}
	$e_val = ($e_val eq "100")? 100 : substr($e_val,0,2);
	mkdir("$main_dir/num",0777) unless(-e "$main_dir/num");
	if($FORM{'cnt'} ne "no"){
		$cook = "$FORM{'id'}$FORM{'action'}$FORM{'number'}";
		&cookie;
		if($COOKIE{$cook} ne "$ENV{'REMOTE_ADDR'}$cook"){	
			$read_number++ if(($act eq "view") || ($act eq "multi_view"));
			$down_number++ if($act eq "down_file");
		}
		&lock if($CONF{'speed_up'} eq "no");
		open(NFI,">$main_dir/num/$FORM{'number'}"); 
		print NFI "$ans_number|$read_number|$down_number|$man_cnt|$pnt_val|";
		close(NFI);
		&unlock if($CONF{'speed_up'} eq "no");
	}
}

sub file_type{
	my(@name_data, @cgi, @file_type, $n_num);
	#-------------------------------------- [ 파일종류 아이콘 ]---------------------------
	@down_data = split(/\|/,$data[5]);
	$file_name = "$down_data[5]";
	if($down_data[4] ne ""){
		@name_data = split(/\//,$down_data[4]);
		$n_num = @name_data - 1;
		if($name_data[$n_num] =~/\?/){
			@cgi = split(/\?/,"$name_data[$n_num]");
			$file_name = $cgi[1];
		}
		else{
			$file_name = "$name_data[$n_num]";
		}
		$f_name = "$down_data[4]";
		$file_size = "링크파일";
	}
	else{
		$f_name = $file_name;
		$file_size = "$down_data[6]";
	}
	$f_name = &encode("$f_name");
	@file_type = split(/\./,$file_name);
	$type = $file_type[$#file_type];
	$type = "cgi" if(($NT && ($file_type[$#file_type] eq "txt") && (($file_type[$#file_type-1] eq "cgi") || ($file_type[$#file_type-1] eq "pl") || ($file_type[$#file_type-1] eq "asp"))) || (($file_type[$#file_type] eq "txt") && (($file_type[$#file_type-1] =~ "php"))));
}

sub file_icon{ # 아이콘 이름 추출 함수... ^.^ 원하시는 종류를 추가하시면 됩니다...
	my(@icon, @quick_img);
	my($type, $type_ok);
	$type = $_[0];
	$type_ok = "0";
	@icon = qw(unknown comp img exe cgi ra html mpeg);
	@1 = qw(rar arj ace tar gz lzh tgz iso);  #comp.gif 압축파일
	@2 = qw(pcx png tif psd ico);     #img.gif 그림파일
	@3 = qw(bat com);             #exe.gif 실행파일
	@4 = qw(asp pl pm php php3 php4);           #cgi.gif 문서파일
	@5 = qw(ram);                 #ra.gif 리얼오디오
	@6 = qw(shtml swf fla css);   #htm.gif html 파일 
	@7 = qw(mpg mov avi rm asx asf wma); #mpeg.gif 동영상파일

	@quick_img = qw(zip hwp mp3 htm html ra wav mid gif jpg jpeg bmp exe cgi doc ppt xls mpeg txt pdf);  
	#quick_img에는 가장 많이 사용하는 확장자 이미지를 type디렉에 넣으시고 배열에 추가시키셔면 됩니다...

	for(0..$#quick_img){
		if(lc($type) eq "$quick_img[$_]"){
			$type = "$quick_img[$_].gif";
			$type_ok = "1";
			last;
		}
	}
	if($type_ok ne "1"){
		EXIT: for(1..$#icon){
			for $key(@$_){ 
				if(lc($type) eq $key){
					$type = "$icon[$_].gif";
					$type_ok = "1";
					last EXIT;
				}
			}
		}
	}
	if($type_ok ne "1"){
		$type = "$icon[0].gif";
	}
	return "$type";
}

sub read_notice{
	my(@gul, @notice, $subject, $comment, $wdate, $die_year, $die_mon, $die_day, $notice_td, $href);
	#========================[ 공지사항 체크 ]========================================
		if((-e "$main_dir/notice.cgi") && ($act ne "view")){
			open(FILE,"$main_dir/notice.cgi");
			@gul = <FILE>;
			@notice = split(/\|/,$gul[0]);
			close (FILE);
			$subject = "<b>$notice[1]</b>";
			#---------------------------------------------------[간단의견 갯수조회]
			if((-e "$main_dir/etc/notice.cgi") && ($CONF{'etc_ok'} eq "yes")){
				open(ETC, "$main_dir/etc/notice.cgi");	
				my @etc = grep(!/^\s/, <ETC>);
				close(ETC);
				my $etc_num = $#etc+1;
				$subject .= (-M "$main_dir/etc/notice.cgi" < 1.0) ? " <font size=1 face=Tahoma color=$CONF{'date_color'}>[$etc_num]</font>" : " <font size=1 face=Tahoma>[$etc_num]</font>" if($etc_num > 0);	
			}
			#---------------------------------------------------
			$comment = $notice[2];
			$wdate = substr($notice[3],5,5);
			($die_year, $die_mon, $die_day) = split(/:/, $notice[4]);
			open(NUM,"$main_dir/num/notice.cgi");
			$read_num = <NUM>;
			$read_num = $read_num ? "$read_num" : "0";
			close(NUM);

			&get_date;
			$notice_td = "<td align=center>^.^</td>" if($CONF{'down_view'} eq "yes");

			if($NT eq "0"){
				$href = "javascript:newin(400, 267, 'sendmail.cgi?name=$CONF{'admin_name'}&mail=$CONF{'admin_mail'}&prog=$sendmail&home_url=$CONF{'home_url'}','spmail')";
			}
			else{
				$href = "mailto:$CONF{'admin_mail'}"; 
			}

			my($ad_mail, $ad_home, $ad_td, $pnt_td, $colspan, $color_val, $over_msg, $notice_line);
			$ad_mail = "<a href=\"$href\"><img src=$img_url/mail.gif width=14 height=10 border=0 alt=\"$CONF{'admin_mail'}\"></a>";
			$ad_home = "<a href=\"$CONF{'home_url'}\" target=_blank><img src=$img_url/home.gif width=10 height=10 border=0 alt=\"$CONF{'home_url'}\"></a>";
			$ad_td = "<td align=center>$ad_mail $ad_home</td>" if($CONF{'i_opt'} eq "yes");
			$pnt_td = "<td align=center><img src=$face_url/80.gif width=16 height=16 alt=\"Smile~ ^.^\"></td>" if($CONF{'point_ok'} eq "yes");
			$colspan = ($CONF{'kind_view'} eq "yes")? "colspan=4" : "colspan=3";
			$color_val = $CONF{'ncel_color'} ? "bgcolor=$CONF{'ncel_color'}" : "";
			$over_msg = &over(" ▷▷ 공지사항 보기");
			$notice_line = "<tr $color_val $over_msg><td $colspan><img src=$img_url/notice.gif> <a href=$cgi_url?id=$FORM{'id'}&action=view_notice><font color=$CONF{'font_color'}>$subject</font></a></td><td align=center>$CONF{'admin_name'}</td>$ad_td<td align=center><font color=$CONF{'date_color'}>$wdate</font></td><td align=center><font color=$CONF{'read_color'}>$read_num</font></td>$notice_td $pnt_td</tr>\n";

			if($die_year eq $year){
				if($die_day  < $mday){
					if($die_mon <= $mon){
						unlink ("$main_dir/notice.cgi") ;
						unlink ("$main_dir/num/notice.cgi");
						unlink ("$main_dir/etc/notice.cgi");
					}
					else{
						print $notice_line;
					}
				}
				else{
					if($die_mon < $mon){
						unlink ("$main_dir/notice.cgi") ;
						unlink ("$main_dir/num/notice.cgi");
						unlink ("$main_dir/etc/notice.cgi");
					}
					else{
						print $notice_line;
					}
				}
			}
			elsif($die_year > $year){
						print $notice_line;
			}
			else{
				unlink ("$main_dir/notice.cgi") ;
				unlink ("$main_dir/num/notice.cgi");
				unlink ("$main_dir/etc/notice.cgi");
			}
		}
#========================[ 공지사항 체크 끝]========================================
}

sub for_print {
	my(@gul_list, @gul_data, @cmt_data, @auto, @etc_data, $wpass, $name, $mail, $mail_val, $home, $home_val, $auto_br, $auto_link, $open_val, $subject, $wdate);
	open(GUL, "$main_dir/data/$FORM{'number'}");
	@gul_list = <GUL>;
	close(GUL);
	$wpass = chomp($gul_list[0]);
	@gul_data = split(/\|/, $gul_list[1]);
	$name = $gul_data[0];
	$mail = $gul_data[1];
	$mail_val = "<br><b>전자우편</b>: $mail" if($mail);
	$home = $gul_data[2];
	$home_val = "<br><b>홈페이지</b>: $home" if($home);
	$subject= $gul_data[3];
	$wdate = $gul_data[4]; 
	@cmt_data = split(/\|/,$gul_list[2]);
	@auto = split(/\|/, $gul_list[4]);
	$auto_link = $auto[0];
	$auto_br = $auto[1];
	$open_val = $auto[2];

	if($open_val eq "no"){
		$pass = $FORM{'passwd'};
		if(($pass ne $CONF{'admin_pwd'}) && ($pass ne $wpass)){
				&error("haker");			
		}
	}

	if ($cmt_data[0] ne "1") { 
        $cmt_data[1] =~ s/<br>/\r\n/g;
	    $cmt_data[1] =~ s/</&lt;/g;
        $cmt_data[1] =~ s/>/&gt;/g;
        $cmt_data[1] =~ s/"/&quot;/g;
	#   $cmt_data[1] =~ s/&lt;hr&gt;/<hr>/g;
		$cmt_data[1] =~ s/\r?\n/<br>/g;
		$cmt_data[1] =~ s/<br>/\r\n/g if($auto_br ne "1");
	}
	else{
		$cmt_data[1] =~ s/<br>/\r\n/g if($auto_br ne "1");
		$cmt_data[1] =~ s/&lt;br&gt;/<br>/g;
		$cmt_data[1] =~ s/&lt;p&gt;/<p>/g;
	}

	if($auto_link eq "1"){
		$cmt_data[1] =~ s/(http:\/\/|https:\/\/|mms:\/\/|ftp:\/\/)([\w\+\-\/\=\?\.\~\&\;\:\xA1-\xFE]+)/<img src=$img_url\/home.gif width=10 height=10> <a href=\"$1$2\" target=\"_blank\"><font color=$CONF{'link_color'}>$1$2<\/font><\/a>/g;

		if($NT eq "1"){
			$cmt_data[1] =~ s/([\w\-]+\@[\w\-+\.]+[\w\-]+)/<img src=$img_url\/mail.gif width=14 height=10> <a href=\"mailto:$1\"><font color=$CONF{'link_color'}>$1<\/font><\/a>/g;
		}
		else{
			$cmt_data[1] =~ s/([\w\-]+\@[\w\-+\.]+[\w\-]+)/<img src=$img_url\/mail.gif width=14 height=10> <a href=\"javascript:newin(400, 267, 'sendmail.cgi?name=$1&mail=$1&prog=$sendmail&home_url=$CONF{'home_url'}','spmail')\"><font color=$CONF{'link_color'}>$1<\/font><\/a>/g;			
		}
	}
	$cmt_data[1] = &view_dongury("$cmt_data[1]") if($CONF{'dgr_ok'} eq "yes");

	@etc_data = split(/\|/,$gul_list[5]);

	my(@name_data, @cgi, @file_type, $file_name, $n_num, $type, $img_view); 
	if($etc_data[5] ne ""){
		$file_name = $etc_data[5];
	}
	else{
		@name_data = split(/\//,$etc_data[4]);
		$n_num = @name_data - 1;
		if($name_data[$n_num] =~/\?/){
			@cgi = split(/\?/,"$name_data[$n_num]");
			$file_name = $cgi[1];
		}
		else{
			$file_name = "$name_data[$n_num]";
		}
	}
	@file_type = split(/\./,$file_name);
	$type = $file_type[1];
	$type = lc($type);
	if(($type eq "gif") || ($type eq "jpg") || ($type eq "jpeg")){
		if($etc_data[4] ne ""){
			$img_view = "<p><img src=$etc_data[4]></p>";
		}
		else{
			$img_view = "<p><img src=$main_url/files/$etc_data[5]></p>";
		}
	}

	my($os_data) = $ENV{'HTTP_USER_AGENT'};
	$CONF{'font_size'} = "10pt"; #프린트 할때 글자 크기... ^^
	print "Content-type: text/html\n\n";
	print "<!---------------------------------------------------------------------------------->\n";
	print "<!-- ★ Program Name - Sepal's SPBOARD v4.5                                       -->\n";
	print "<!-- ★ Programer - Kim se youn (madgf\@hanmail.net, sepal\@spfamily.com)           -->\n";
	print "<!-- ★ Homepage - http://sepal.woorizip.com or http://www.spfamily.com           -->\n";
	print "<!-- ★ Last Modified - 2001.12.12                                                -->\n";
	print "<!--                                                         ★ made in KOREA ★  -->\n";
	print "<!---------------------------------------------------------------------------------->\n\n";
	print "<html>\n";
	print "<head><title>$subject</title>\n";
	print "<meta http-equiv=\"Content-Type\" content=\"text/html; charset=EUC-KR\">\n";
	print "<style type='text/css'>\n";
	print "<!--\n";
	print "A:link {color:$CONF{'link_color'};text-decoration:none;}\n";
	print "A:visited {color:$CONF{'link_color'};text-decoration:none;}\n";
	print "A:active {color:red;text-decoration:underline;}\n";
	print "A:hover {color:#043BFF;text-decoration:underline;}\n";
	print ".ver8 {color:#000000;font-size:8pt}\n";
	print ".ver9 {color:#000000;font-size:$CONF{'font_size'}}\n";
	print "p,br,body,td,select,input,form,textarea,option {font-size:$CONF{'font_size'};font-family:$CONF{'font_kind'};}\n";
	print ".cont{line-height:140%}\n" if(($os_data =~ /MSIE/) || ($FORM{'img'} ne "ok")); #넷스케이프에서 이미지파일일때 line-height 옵션 없애기...
	print "-->\n";
	print "</style>\n";
	print "</head>\n";
	print "<body bgcolor=#ffffff>\n";
	&Thead("100%","","3","");
	print "<tr><td class=cont>\n";
	print "<font size=3 face=굴림><b>제목</b>: <u>$subject</u></font><p>\n";
	print "<font size=2 face=굴림><b>작성자</b>: $name<br><b>작성일</b>: $wdate\n";
	print "$mail_val $home_val<p>\n";
	print "$cmt_data[1]</font>\n";
	print "$img_view\n" if($img_view);
	print "</td></tr>\n";
	&Tfoot;
	print "</body></html>\n";
	exit;
}

