}

sub send_gul{
	my(@list, @sub_list, @seper, @etc_data, $chk_opt, $pass, $name, $mail, $wmail_to, $home, $subject, $wdate, $pre_view, $file_name, $mailto, $auto_link, $auto_br, $open_val);
	$mail_addr = $_[0];
	$chk_opt = $_[2];
	if($mail_addr ne ""){
		$FORM{'tmail'} = $mail_addr;
		$FORM{'tname'} = $CONF{'admin_name'};
		$FORM{'number'} = $_[1];
	}
	if($FORM{'tmail'} eq ""){
		&error("send");
	}
	unless($FORM{'tmail'} =~ /.*\@.*\..*/){
		&error("send");
	}
	open(GUL, "$main_dir/data/$FORM{'number'}");
	@list = <GUL>;
	close(GUL);
	$wpass = chomp($list[0]);
	@sub_list = split(/\|/, $list[1]);
	$name = $sub_list[0];
	$mail = $sub_list[1];
	$wmail_to = $mail ? "<a href=mailto:$mail>$name</a>" : "$name";
	$home = $sub_list[2];
	$subject = $sub_list[3];
	$wdate = $sub_list[4];
	@auto = split(/\|/, $list[4]);
	$auto_link = $auto[0];
	$auto_br = $auto[1];
	$open_val = $auto[2];
	if(($open_val eq "no") && ($chk_opt ne "chk_no")){
		$pass = $FORM{'passwd'};
		if(($pass ne $CONF{'admin_pwd'}) && ($pass ne $wpass)){
				&error("haker");			
		}
	}
	@etc_data = split(/\|/,$list[5]);
	$pre_view = $etc_data[3];
	$file_name = (($etc_data[5] eq "") && ($etc_data[4] eq ""))? "" : "$etc_data[5]";
	@seper = split(/\|/,$list[2]);
	if ($seper[0] ne "1") {
        $seper[1] =~ s/<br>/\r\n/g;
	    $seper[1] =~ s/</&lt;/g;
        $seper[1] =~ s/>/&gt;/g;
        $seper[1] =~ s/"/&quot;/g;
	#   $seper[1] =~ s/&lt;hr&gt;/<hr>/g;
		$seper[1] =~ s/\r?\n/<br>/g;
		$seper[1] =~ s/<br>/\r\n/g if($auto_br ne "1");
	}
	else{
		$seper[1] =~ s/<br>/\r\n/g if($auto_br ne "1");
		$seper[1] =~ s/&lt;br&gt;/<br>/g;
		$seper[1] =~ s/&lt;p&gt;/<p>/g;
	}
#	if($auto_link eq "1"){
#		$seper[1]=~ s/(http:\/\/|https:\/\/|mms:\/\/|ftp:\/\/)([\w\+\-\/\=\?\.\~\&\;\:\xA1-\xFE]+)/<a href=\"$1$2\" target=\"_blank\"><font color=$CONF{'head_color'}>$1$2<\/font><\/a>/g;
#		$seper[1]=~ s/([\w\-]+\@[\w\-+\.]+[\w\-]+)/<a href=\"mailto:$1\"><font color=$CONF{'head_color'}>$1<\/font><\/a>/g;
#	}

	$mailto = ($FORM{'fmail'}) ? "<a href=mailto:$FORM{'fmail'}>$FORM{'fname'}</a>" : "$FORM{'fname'}";
	$FORM{'comment'} =~ s/\r\n/<br>/g;
	&get_date;
	if($mail_addr ne ""){
		$FORM{'fname'} = $sub_list[0];
		$FORM{'fmail'} = $sub_list[1];
	}
	my $to_mail = "$FORM{'tname'} <$FORM{'tmail'}>";
	my $from_mail = "$FORM{'fname'} <$FORM{'fmail'}>";
	open (MAIL, "|$sendmail -t") || die "화일 열기 실패!\n";
	print MAIL "To: $to_mail\n";
	print MAIL "From: $from_mail\n";
	print MAIL "Subject: 게시물이 배달 되었습니다.\n";
	print MAIL "Content-Type: text/html; charset=euc-kr\n";
	print MAIL "<style type=text/css>\n";
	print MAIL "a:link, a:visited, a:active { text-decoration:none; color:$CONF{'link_color'}; }\n";
	print MAIL "a:hover { text-decoration:underline; color:red; }\n";
	print MAIL "td {font-size:9pt; font-family:$CONF{'font_kind'};}\n";
	print MAIL ".main {font-size:9pt; font-family:$CONF{'font_kind'};}\n";
	print MAIL "</style>\n";
	print MAIL "<div align=center><table width=100% cellpadding=1 cellspacing=0 bgcolor=$CONF{'head_color'}>\n";
	print MAIL "<tr><td align=center><font class=main color=$CONF{'hfont_color'}>▣$subject▣</font></td></tr>\n";
	print MAIL "<tr><td>\n";	
	$CONF{'cel_color1'} ||= "#ffffff";
	print MAIL "<table width=100% cellpadding=3 cellspacing=0 bgcolor=\"$CONF{'cel_color1'}\" background=\"$CONF{'background'}\">\n";
	print MAIL "<tr><td><font class=main color=$CONF{'font_color'}>\n";
	print MAIL "보낸 날짜: $date<br>\n";
	print MAIL "보낸 사람:$mailto<br>\n" if($mailto);
	print MAIL "Message: $FORM{'comment'}\n" if($FORM{'comment'});
	print MAIL "</font></td></tr>\n";	
	print MAIL "<tr><td>\n";		
	print MAIL "<font class=main color=$CONF{'font_color'}>\n";
	print MAIL "게시판 DB명: $FORM{'id'}<br>\n";	
	print MAIL "작성자: $wmail_to<br>\n";
	print MAIL "홈 URL: <a href=$home target=_blank>$home</a><br>\n" if($home);
	print MAIL "제목:$subject<br>\n";		
	print MAIL "<hr color=$CONF{'head_color'} size=1\">\n";
	print MAIL "<font class=main color=$CONF{'font_color'}>$seper[1]\n";
	print MAIL "<p align=right>This message from <a href=$CONF{'home_url'} target=_blank>$CONF{'home_url'}</a>&nbsp; &nbsp; </p></font>\n";
    print MAIL "</font></td></tr>\n";
	print MAIL "</table>\n";
	print MAIL "</td></tr>\n";	
	print MAIL "</table></div>\n";
	close (MAIL);
	if($mail_addr eq ""){
		local $send_end = "1";
		&load_page;
	}
}

sub view {
	my($chk_num, $dis_opt) = @_;
	my(@views, @Temp, @auto, @etc_data, @cgi, @file_type, @md, @tm, @seper, $auto_link, $auto_br, $domain, $ip, $pre_view, $file_name, $n_num, $f_name, $file_size, $type, $down_msg, $img_view, $file_down, $down_count, $w_info, $pdate,  $year, $month, $sday,  $stime, $smin, $mod_date, $homepage);
	local($read_number, $down_number, $man_cnt, $pnt_val, $e_val, $wpass, $pass);
	$FORM{'number'} = "$chk_num" if($chk_num ne "");

	open(VFILE, "$main_dir/data/$FORM{'number'}");
	@views = <VFILE>;
	close(VFILE);

	chomp($wpass = $views[0]);
	@Temp = split(/\|/, $views[1]);
	@auto = split(/\|/, $views[4]);
	$auto_link = $auto[0];
	$auto_br = $auto[1];
	$open_val = $auto[2];

	if($open_val eq "no"){
		if($FORM{'passwd'} eq ""){
			&open_login("$Temp[3]");
			next if($act eq "multi_view");
		}
		else{
			$pass = crypt($FORM{'passwd'},"sp");

			if($FORM{'number'} =~ /-/){
				my @org = split(/-/,$FORM{'number'});
				my($org_num) = $org[0];
				open(ORG, "$main_dir/data/$org_num.cgi");
				my @org_data = <ORG>;
				close(ORG);
				my @open = split(/\|/,$org_data[4]);
				my $open_ok = $open[2];
				chomp($wpass = $org_data[0]) if($open_ok eq "no"); 
			}

			unless(($pass eq $CONF{'admin_pwd'}) || ($pass eq $wpass)){
				&error("pass");			
			}
			#답변등록으로 비공개글 보기 막기...
			print "Set-Cookie: OPEN=$FORM{'id'}_$FORM{'number'};\r\n";
		}
	}

	unless($views[4] =~ /\|/) { #3차 업그레이드 자를 위해... 나중에 삭제????
		$auto_br = "1";
		$auto_link = "1" if(chomp($views[4]) eq "1");
	}
	&plus_num;
	@etc_data = split(/\|/,$views[5]);
	$ip=$etc_data[1];
	$domain=$etc_data[2];
	if(($etc_data[5] eq "") && ($etc_data[4] eq "")) {
		$down_number = "*"; $img_view = "";	$file_down = ""; $pre_view = "";
	}
	else{
		$down_number = $down_number? "$down_number" : "0";
#-----------------------------------------	
		$file_name = "$etc_data[5]";
		if($etc_data[4] ne ""){
			@name_data = split(/\//,$etc_data[4]);
			$n_num = @name_data - 1;
			if($name_data[$n_num] =~/\?/){
				@cgi = split(/\?/,"$name_data[$n_num]");
				$file_name = $cgi[1];
			}
			else{
				$file_name = "$name_data[$n_num]";
			}
			$f_name = "$etc_data[4]";
			$file_size = "링크파일";
		}
		else{
			$f_name = $file_name;
			$file_size = "$etc_data[6]";
		}
		$f_name = &encode("$f_name");
		@file_type = split(/\./,$file_name);
		$type = $file_type[$#file_type];
		$type = lc($type);

		if(($type eq "gif") || ($type eq "jpg") || ($type eq "jpeg")){
			$down_msg = "다운받으시려면 그림 위에서 마우스 오른쪽 버튼을 누르신뒤 다른이름으로 그림저장(익스플로러).. Save Images As(넷스케이프)를 선택하세요...";
			if($etc_data[4] ne ""){
				$img_view = "<p align=center><img src=$etc_data[4] alt=\"$down_msg\"></p>";
			}
			else{
				$img_view = "<p align=center><img src=$main_url/files/$etc_data[5] alt=\"$down_msg\"><p>";
			}
		}
		else{ $img_view=""; }
#-------------------------------------------------	

