$value17="$read_count3";
$abs{$_}=$value17;
}#foreach
@ddds3 = sort sorted @_;

foreach $sss3(reverse sort sorted @ddds3){
@data3 = split(/\|/, $sss3);

if(-f "$data_dir/$data3[12]/$data3[0]"){
   $page_cnt_tmp3 ++;

$count3++;
$check_count3++;


  open(USER_ID_CHECK, "$dir/user_check/$ENV{'REMOTE_ADDR'}");
  $user_id_check_value=<USER_ID_CHECK>;
  close(USER_ID_CHECK);
	if(-f "$dir/user_type/$user_id_check_value"){
