		  $data3[37]=~s/\s//g;
		  $data3[7]="$data3[37]";
		}
	}


print <<END;
<td bgColor='$list_back' align='center' valign='_top'><font color=$list_text>
END
if ($data3[2]  ne ""){
  @different_array3 = split(/:::/, $data3[36]);
	if(-f "$photo_images_dir/$different_array3[1]"){
	  print "<a href=$prog_url/webcart.cgi?mode=read&cate=$data3[12]&item=$data3[0]&page=$FORM{'page'}&mid_cate=$FORM{'mid_cate'}&top_cate=$data3[12]><img src=$photo_images_url/$different_array3[1] height=110 border=0></a>";
	}elsif($data3[33] eq "" || $data3[33] eq "0"){
	  print "<a href=$prog_url/webcart.cgi?mode=read&cate=$data3[12]&item=$data3[0]&page=$FORM{'page'}&mid_cate=$FORM{'mid_cate'}&top_cate=$data3[12]><img src=$item_images_url/$data3[2] height=110 border=0></a>";
	}else{
