	}
}else{
	if ($data3[34] eq ""){
	  print "<a href=$prog_url/webcart.cgi?mode=read&cate=$data3[12]&item=$data3[0]&page=$FORM{'page'}&mid_cate=$FORM{'mid_cate'}&top_cate=$data3[12]><img src=$images_url/no_images.gif border=0 height=110 border=1></a>";
	}
