print <<END;
</font>
END

print "<br><a href=$prog_url/webcart.cgi?mode=read&cate=$data3[12]&item=$data3[0]&page=$FORM{'page'}&mid_cate=$FORM{'mid_cate'}&top_cate=$data3[12]><font color=#404088>$data3[1]</font></a><br><font color=#E5005C>";

	if ($per_si eq "1"){
