@lovelines = map  { $_->[0]                              }
                 sort { $b->[3] <=> $a->[3]                  }
                 grep { $id ne $_->[1] && $_->[3] >= $def_lm }
                 grep { $ibsc || $_->[2] ne $sx              }
                 map  { [$_,(split(/<>/))[0,3],
                        &love_match($uv,(split(/<>/))[19])]  } @alllines;

