if($mode eq search){&search;}
if($mode eq edelform){&edelform;}	
&head;									
&treeview;								
&tail;									
exit;

#======헤드부분=========#
sub head{
print "Content-Type: text/html\n\n";
print <<"HEAD";
<html>
<head>
<meta http-equiv="Content-Type" content="text; charset=EUC-KR">
$styleler
<body leftmargin="0" topmargin="0" marginwidth="0" marginheight="0">
HEAD
}
#-----하단부분-------#
sub tail{
print <<"TAIL";
</body></html>
TAIL
exit;
}
#***리스트출력***#
sub treeview{

&get_time;

open (DATA, "$datafile") || &error("$datafile을열수가없음");
@Data = <DATA>;
close (DATA);
$total=@Data;

# 본문글과댓글수추출
for($i=0; $i<$total; $i++){
($treeno,$mesno,$offset,$babo,$subject,$contents,$time,$ip,$pass,$mday,$count) = split(/<>/, $Data[$i]);
@dir = 0;
foreach $q (@dir){
if ($offset =~ /$q/){$idd{$q}++;}
  }
}
foreach $a (@dir){
$toronum=$idd{$a}; #본문글수
$renums=$total-$idd{$a}; #댓글수

print "<center><table cellspacing=1 cellpadding=4 bgcolor=black width=450><tr>\n";

if($total > 0){print "<td bgcolor=ffffff align=center width=50>번호</td><td bgcolor=ffffff align=center width=210>제목</td><td bgcolor=ffffff align=center width=70>이름</td><td bgcolor=ffffff align=center width=70>날짜</td><td bgcolor=ffffff align=center width=50>조회</td></tr>";}

# 페이지나누기및리스트출력
if(($cgi{'page'} eq "") || ($cgi{'page'} eq 0)) {$page=1;}
else {$page = $cgi{'page'};}

$tot_page= int($total/$treemax);
if($tot_page < ($total/$treemax)) {$tot_page = $tot_page+1;}

$next_p = $page+1;
$pre_p = $page-1;
$count=$total-1;

if ($pre_p <= 0) {$start_count = $count; }
else {$start_count = $count - ($pre_p*$treemax); }
$dis_count = $count - ($page*$treemax);

for ($j=0;$j<=$count;$j++) {$h = $count - $j;

if(($h> $start_count) || ($h-1< $dis_count)) { next; }
$Message = $Data[$h];
@TempData = split (/<>/, $Message);

$TempData[4] = substr($TempData[4],0,40);
if($TempData[2] == 0){$jokas="$TempData[0]";}else{$jokas="$TempData[0]\-$TempData[2]";}

if($TempData[2] == 0){
print "<td bgcolor=ffffff align=center>$jokas</td>";
print "<td bgcolor=ffffff><a href=\"$program?mode=countt&pmode=viewcnt&treenum=$TempData[0]&datanum=$TempData[1]&delno=$TempData[6]&page=$page\" title=\"$TempData[3]님이남기신글\">$TempData[4] </a> $nnmark </td><td bgcolor=ffffff align=center>$TempData[3]</td><td bgcolor=ffffff align=center>$TempData[9]</td><td bgcolor=ffffff align=center>$TempData[10]</td></tr>";
}

else{
print "<td bgcolor=ffffff align=center>$jokas</td><td bgcolor=ffffff>\n";
for($i=0;$i<$TempData[2];$i++){print "&nbsp;";}
print "<font face=돋움 style = font-size:9pt>$redori <a href=\"$program?mode=countt&pmode=viewcnt&treenum=$TempData[0]&datanum=$TempData[1]&delno=$TempData[6]&page=$page\" title=\"$TempData[3]님이남기신글\">$TempData[4] </a> $nnmark </td><td bgcolor=ffffff align=center>$TempData[3]</td><td bgcolor=ffffff align=center>$TempData[9]</td><td bgcolor=ffffff align=center>$TempData[10]</td></tr>";}
}

print"</table><br><center>$lineco<table cellspacing=1 cellpadding=4 bgcolor=black width=450><tr>";

# 페이지버튼
print "<td bgcolor=ffffff width=405 align=center>\|\n";

for($i=1;$i<=$tot_page;$i++){
   if ($i == $page) {
   print "<b>$i</b> |\n";
   }
   else {
   print "<a href=$program?page=$i>$i</a> |\n";
   }
}

print "</td>\n";
if($total > 0){print "<td bgcolor=ffffff width=45 align=center><a href=$program?mode=form&pmode=new>글쓰기</a></td></tr></table>\n";}
}
}

#=====개별내용출력=======#
sub selectview{
&get_time;
&head;
open(SEL,"$datafile")||&error("$datafile을열수가없음");
@seldata=<SEL>;
close(SEL);

$linecount='0';
foreach $seldata(@seldata){
local($treeno,$mesno,$offset,$babo,$subject,$contents,$time,$ip,$pass,$mday,$count)=split(/<>/,$seldata);

        if($treenum == $treeno and $datanum == $mesno){

        if($reshid eq "yes"){$resher="\[<a href=$program?mode=resset&treenum=$treeno&datanum=$mesno&offset=$offset>댓글</a>\]";}else{$resher="";}

        if($ipvist eq "yes"){$iptec="[$ip]";}else{$iptec="";}

	print "<div align=center><table cellspacing=1 cellpadding=4 bgcolor=black width=450><tr><td bgcolor=ffffff>\n";
	print "제목\ : $subject$nnmark</td></tr><tr>\n";
	print "<td bgcolor=ffffff><br>\n";
	print "$contents<br><br></td></tr><tr>";
	print "<td bgcolor=ffffff>글쓴이 : $babo 조회수 : $count &nbsp;$resher\n";

print<<"eof";
 [<a href=$program?mode=edelform&pmode=ed&treenum=$treenum&datanum=$datanum&offset=$offset&edtno=$time>수정</a>]
 [<a href=$program?mode=edelform&pmode=no&treenum=$treenum&datanum=$datanum&offset=$offset&edtno=$time>삭제</a>]
 [<a href=$program?page=$page>목록보기</a>]
eof
print "</td></tr></table></div><br>\n";
}
$linecount++;
}
&treeview;
&tail;
}
