sub form{

&head;
if($mode eq 'resset'){$thtitle='번글에 댓글쓰기';}
else{$thtitle='새글쓰기';}
$offsett=$offset-1;
if($mode eq 'resset'){if($offset > 1){$dryic="$treenum\-$offsett";}else{$dryic="$treenum";}}
if($admwrt eq "yes"){$adwr="관리자만글쓰기가가능합니다.";$adwrps="관리자암호";}else{$adwr="";$adwrps="암호";}
print <<"FORM";
<script>da=new Date();y=da.getYear();e=da.getMonth()+1;h=da.getHours();m=da.getMinutes();s=da.getSeconds();
d=da.getDate();day=y+"년"+e+"월"+d+"일"+h+"시"+m+"분"+s+"초";function asd(){document.forms[0].sstime.value=day;}
window.onload=asd;</script>
<div align=center>$adwr
<form method=POST action=$program>
<input type=hidden name=mode value=postdata>
<input type=hidden name=treenum value=$treenum>
<input type=hidden name=datanum value=$datanum>
<input type=hidden name=offset value=$offset>
<input type=hidden name=pmode value=$pmode>
<input type=hidden name=sstime value="">
<table cellspacing=1 cellpadding=4 bgcolor=black width=450>
<tr><td bgcolor=ffffff align=right>* $dryic $thtitle - 웹주소나 메일은 자동링크</td></tr>
<tr><td bgcolor=ffffff align=right>이름 <input type=text name=babo size=12 maxlength=8 value=$babo></td></tr>
<tr><td bgcolor=ffffff align=right>제목 <input type=text name=subject size=35 maxlength=30 value=$ressubj></td></tr>
<tr><td bgcolor=ffffff align=right>
<textarea cols=50 rows=9 name=contents scroll=no wrap=hard>$rescont</textarea>
</td></tr>
<tr><td bgcolor=ffffff align=right>$adwrps <input type=password name=pass size=12 maxlength=8 value=$pass> <input type=submit value=저장> <input type=reset value=취소></td></tr></table>
<form>
</div>
FORM
&tail;
}

#====댓글폼출력======#
sub resset{

$treenum=$treenum;
open(POS,"$datafile")||&error("$datafile을열수가없음");
@posdata=<POS>;
close(POS);
$offset++;
$line=0;
foreach $posdata(@posdata){
	local($treeno,$mesno,$offset,$babo,$subject,$contents)=split(/<>/,$posdata);
		if($treenum == $treeno && $datanum == $mesno){
		$contents =~ s/<br>/\n>/g;
		$rescont = "Re:";
		($dummy1,$dummy2)=split(/\^/,$subject);
		($rescounter,$dummy4)=split(/\:/,$dummy2);
		$rescounter++;
			if($dummy1 eq Re){
				$ressubj = "$dummy1\^$rescounter\:$dummy4";
			}
			else{$ressubj = "Re\:$subject";}
		last;
		}
		else{$line++;}

}
if($offset > $resstop){&error("댓글제한수가 초과되서<br>더 이상 댓글을 달수가 없어요.");}
&form;
exit;

}

#====글저장=====#
sub postdata{

$count=0;
$mday=&get_time("$nday");
$date=$cgi{'sstime'};
$pass=$cgi{'pass'};
$host=$ENV{'REMOTE_ADDR'};
if($admwrt eq "yes"){if($pass ne $admin){&error("관리자만글쓰기가가능합니다.!!");}}

open(POS,"$datafile")||&error("$datafile을열수가없음");
@posdata=<POS>;
close(POS);
$totmax=@posdata;

if($totmax eq $maxlin){&error("글저장한도수초과로더이상글저장이불가능합니다.");}

$flag="0";

if($treenum eq ""){
	$treemax=0;
	foreach $posdata(@posdata){
		local($tno,$mno,$of,$bab,$subj,$cont)=split(/<>/,$posdata);
		if($treemax < $tno){$treemax=$tno;}
	}
	$treenum = $treemax+1;
	$offset=0;
	$flag="1";
}

$line=0;
foreach $posdata(@posdata){
	local($treeno,$mesno,$offset,$babo,$subject,$contents)=split(/<>/,$posdata);
		if($treenum == $treeno && $datanum == $mesno){last;}
		else{$line++;}
	if($cgi{'contents'} eq $contents){&error("$treeno번과 같은 글입니다. 중복된 글의 등록은 금지입니다.");}
}

$numbermax=0;
	foreach $posdata(@posdata){
	local($tno,$mno,$of,$bab,$subj,$cont)=split(/<>/,$posdata);
		if($numbermax < $mno){$numbermax=$mno;}
	}
$datanum=$numbermax+1;
if($babo eq ""){&error("이름은필수여요!!");}
if($subject eq ""){&error("제목은필수여요!!");}
if($contents eq ""){&error("내용은필수여요!!");}
if($pmode ne new){
	$m=0;

if($reswich ne "1"){ $linene=$line; $next=$line+1; }else{ $linene=$line-1;
$next=$line; 
}
	$max=$#posdata+1;
	open(POS,">$tempfile")||&error("$tempfile을열수가없음");
	eval{flock(POS,2);}; 
	$newdata="$treenum<>$datanum<>$offset<>$babo<>$subject<>$contents<>$date<>$host<>$pass<>$mday<>$count";

for($m=0;$m <= $linene;$m++){
		print POS "$posdata[$m]";
	}

	print POS "$newdata\n";

for($m=$next;$m<=$max;$m++){
	print POS "$posdata[$m]";
	}

	eval{flock(POS,8);};
	close(POS);
}

elsif($pmode eq new){

	open(POS,">$tempfile")||&error("$tempfile을열수가없음");
	eval{flock(POS,2);};
	$newdata="$treenum<>$datanum<>$offset<>$babo<>$subject<>$contents<>$date<>$host<>$pass<>$mday<>$count";
        print POS @posdata;
	print POS "$newdata\n";
	eval{flock(CINOUT,8);};
	close(POS);
	}

rename($tempfile,$datafile);

&wanryo("성공적으로 저장되었습니다.!!");
}

#====에러출력======#
sub error {
&head;
print <<"ERROR";
<br><p>
<div align=center>
<table style="border:0px #787878 solid"><tr><td align=center class=td>
<h3 style="color:red;font-family:Times new roman">ERROR !</h3>
<P><font style="color:#4b4b4b;">※ $_[0]</font><br>
<br>
[<a href="#" onclick="javascript:history.go(-1);">뒤로</a>]
<P>
</td></tr></table>
</div>
ERROR
&tail;
exit;
}
#===코딩====#
sub decode{
	if($ENV{'REQUEST_METHOD'} eq "POST"){
          if ($ENV{'CONTENT_LENGTH'} > 8200) { &error("글의용량이너무큽니다.");}
	  read(STDIN, $form_data, $ENV{'CONTENT_LENGTH'});
	}else{
	  $form_data = $ENV{'QUERY_STRING'};
	  @pairs = split(/&/, $form_data);
	  	foreach $pair (@pairs) {
		($name,$value)=split(/=/,$pair);
		if($name eq mode){$mode=$value;}
		if($name eq treenum){$treenum=$value;}
		if($name eq datanum){$datanum=$value;}
		if($name eq offset){$offset=$value;}
		if($name eq pmode){$pmode=$value;}
        if($name eq maxtno){$maxtno=$value;}
		}

	}

	  @part = split('&', $form_data); 
	  foreach $i (@part) {            
	    ($valiable, $value) = split('=', $i); 
	    $value =~ tr/+/ /;           
	    $value =~ s/%([0-9a-fA-F][0-9a-fA-F])/pack("C", hex($1))/eg;

	    $value =~ s/&/&amp;/g;	
	    $value =~ s/"/&quot;/g;	
	    $value =~ s/</&lt;/g;	
	    $value =~ s/>/&gt;/g;	
	    $value =~ s/\r\n/<br>/g;	
	    $value =~ s/\n/<br>/g;
	    #$value =~ tr/\r\n//d;	
	    $cgi{$valiable}=$value;

if ($hotwd eq 'yes') {
foreach ( @errhwd ){if ($value =~ /$_(.|\n)*/i) {&error("[$_]:이런 언어는 사용불가 입니다.");}}}

            $host = $ENV{'REMOTE_ADDR'};

if ($denykey eq 'yes') {
foreach ( @deny ){if ($host =~ /$_(.|\n)*/i) {&error("[$_]:이 호스트는 관리자에 의해 게시판사용이 금지 되었습니다.");}}}
	  }

	$mode=$cgi{'mode'};	
	$treenum=$cgi{'treenum'};
	$datanum=$cgi{'datanum'};
	$offset=$cgi{'offset'};
	$subject=$cgi{'subject'};
        $date=$cgi{'sstime'};
	$contents=$cgi{'contents'};
	$pmode=$cgi{'pmode'};
	$delpass=$cgi{'delpass'};
        $delno=$cgi{'delno'};	
        $count=$count;
        $nday=$nday;
        $edtno=$cgi{'edtno'};
        $pass=$cgi{'pass'};
        $sword=$cgi{'sword'};
        $babo=$cgi{'babo'};
        $page = $cgi{'page'};
        $time=$cgi{'time'};
        $page=$page;
}

#==글저장,수정,삭제완료메세지==#
sub wanryo {
&head;
print "<div align=center><BR><BR>$_[0]<br><br>자동으로이동합니다.<br><br>[<a href=\"$program\">자동으로 않가면 요길 눌러요.ㅡㅡ;</a>]<br><br>\n";
print "<script>self.location='$program';</script>\n";
print "</div>\n";
&tail;
exit;
}

#==수정삭제암호확인==#
sub pscheck{
if($cgi{'delpass'} eq ""){error("암호를입력해야종");}
	open(LOG,"$datafile") || &error("$datafile화일을열수가없음");
	@log = <LOG>;
	close(LOG);
	@new = ();
        foreach(@log){
		@data = split(/<>/,$_);

if($edtno eq $data[6]){
             	if($delpass eq $data[8] || $delpass eq $admin){$match=1;}
}		
}
if($match != 1){&error("암호가틀립니다.");}
if($match == 1){if($mode eq "edit"){&edit;}else{&delte;}}
}

#==삭제실행==#
sub delte{

open(IN,"$datafile") || &error("$datafile을열수가없음");
	@lines = <IN>;
	close(IN);

            @new = ();
            if($pmode eq 'rwer'){
		foreach$line(@lines){
			($treeno,$mesno,$offset,$babo,$subject,$contents,$time,$ip,$pass,$mday,$count) = split(/<>/,$line);
			if($time eq $edtno){
               	$line = "";
			}
			push(@new,$line);
		}
}
	open(OUT,">$datafile") || &error("$datafile을읽을수가없음");
	print OUT @new;
	close(OUT);
&wanryo("성공적으로 삭제되었습니다.!!");
}

