 를 사용하는 것과

	tie %NOTE,AnyDBM_File,"$$DBM",O_RDONLY,0666;
	@$DBM_TYPE1 = split(/\s/,$NOTE{"$DBM_KEY1"});
	@$DBM_TYPE2 = split(/\s/,$NOTE{"$DBM_KEY2"}) if $_[2];
