#!/usr/bin/perl
use CGI;
$q = CGI->new();
$pagepersize = 4;

$q->header(-charset=>'euc-kr');
if ($q->param('page') eq "") {
	$page = 1;
}else {
	$page = $q->param('page');
}

&html_of_start;

$totalrecord = &get_of_max;
$startnum = $totalrecord -($q->param('page') - 1) * $pagepersize - 1;
$endnum = $startnum - $pagepersize + 1;
$endnum < 0 && ($endnum = 0);
&get_of_data;
if ($startnum < 0) {
	print "<tr align=center bgcolor=#eeeeee>\n";
	print "<td colspan=6><small><b>등록된 자료가 없습니다</b>";
	print "</small></td>\n";
	print "<tr>";
}else {
	for($i=$startnum;$i<$endnum;$i--){
		if (($startnum - $i) % 2 eq 0) {print "<tr align=center>\n";}
		else {print "<tr align=center bgcolor=#eeeeee>\n";}

		print <<HTML_START;
			<td><small>$field[$i]</small></td>
			<td><small>$field[$i]</small></td>
			<td><small>$field[$i]</small></td>
			<td><a href="detail.cgi?qry=$field[$i]"><img src="picsdata/$field[$i]" width=75 height=120 border=0></a></td>
			<td><small>$field[$i]</small></td>
			<td><small>$field[$i]</small></td>
			<td><small>$field[$i]</small></td>
			</tr>
HTML_START
}
}							

	print <<HTML_START;
		</table>
			<br>
			<table border=0><tr>
			<td><small><b><a herf="main.htm">홈</a></b></small></td>
			<td><font size=-2>
HTML_START

	$lastpage = $totalrecord / $pagepersize;
	if ($totalrecord % $pagepersize ne 0) { $lastpage ++;}

	$prevpage = $q->param('page') - 1;
	$nextpage = $q->param('page') + 1;

	($prevpage > 0) && print " <a href=pics.cgi?page=$prevpage>prev</a>\n";
	for($i=1;$i<=$lastpage;$i++){
		if ($i eq $q->param('page')) {print " [$i]\n";}
		else { print " <a href=pics.cgi?page$i>[$i]</a>\n";}
}
	($nextpage <= $lastpage) && print " <a href=pics.cgi?page=$nextpage>Next</a>\n";

	print <<HTML_START;
		</font></td>
			<td><a href="newregister.htm">등록하기</a></td>
			</tr></table>
			<br>
			<hr size=1>
			</body>
			</html>
HTML_START

##################################서브루틴 위치자리########################################################
#데이타베이스에서 첫필드의 최대수를 구하는 서브루틴#
sub get_of_max {							
	&create_of_connection;
		my ($sth, $max);
		$sth = $dbh->prepare("SELECT MAX(num) FROM imagedb");
		$sth->execute;	
		$max = ($sth->fetchrow_array)[0];
		$sth->finish;

	return $max;
}
########################################################################################
#데이타베이스 연결 서브루틴#
sub create_of_connection {
	use DBI;
		my($dsn,$uid,$pwd);
		$dsn = "DBI:ODBC:mypubs";
		$uid = "";
		$pwd = "";
		$dbh = DBI->connect($dsn,$uid,$pwd) || die "연결 실패 $DBI::errstr\n";
		return;
}
########################################################################################
########################################################################################
#html 처음부분 출력하는 서브루틴#
sub html_of_start {
	print <<HTML_START;
		<html><head><title>자세히보기</title></head>
		<body>
		<table border=0>
HTML_START
}
########################################################################################	
#html 끝부분 표시하는 서브루틴#
sub html_of_tail {		
	print <<HTML_START;
		</table>
		<input type=button value="back" onclick="history.back();">
		</body>
		</html>
HTML_START
}
########################################################################################
sub get_of_data {
	&create_of_connection;
	$sql = "select * from imagedb";
	$sth=$dbh->prepare($sql);
	$sth->execute();
	@field=$sth->fetchrow;

	$sth->finish;
	$dbh->disconnect;
return
}

