use DBI;
my $driver = "mysql";
my $database = "test";
my $dbh = DBI->connect("DBI:database=$database;host=$host"$user,$passwd");
my $sth = $dbh->prepare("SELECT * FROM logs);
$sth->execute;
while (my @rows = $sth->fetchrow_array){
     my ($num, $time, $title) = @row;
     print "$num, $time, $title";
     }
</code>
