sub MakeIconStr {
	local (*icon_str) = @_;

	$modify = $_ico'Modify;
	$delete = $_ico'Delete;
	$write = $_ico'Write;
	$reply = $_ico'Reply;
	$prev = $_ico'Prev;
	$next = $_ico'Next;
	$list = $_ico'List;

	$br_tag = ($_wb'TemplateType) ? "<BR>" : "";

	$icon_str = qq|
		$br_tag
		<A HREF="javascript:OnVerify('modify', '$gUserType', '$_wb'ViewInNewWindow')"
			onMouseOver="window.status='Modify this message'; return true;"
			onMouseOut="window.status=''; return true;">
			<IMG SRC="$_wb'IconDir/$modify" BORDER=0 ALT="Modify"></A>$br_tag

		<A HREF="javascript:OnVerify('delete', '$gUserType', '$_wb'ViewInNewWindow')"
			onMouseOver="window.status='Delete this message'; return true;"
			onMouseOut="window.status=''; return true;">
			<IMG SRC="$_wb'IconDir/$delete" BORDER=0 ALT="Delete"></A>$br_tag
	|;

	if ($_wb'ViewInNewWindow) {
		local ($reply_url, $write_url);

		$reply_url =부분생략

		$write_url = "&Mode=write&Block=$form{'Block'}";
		$icon_str .= "<A HREF=\"javascript:OnCallWrite('$_wb'WriteProgram', '$write_url')\"\n";
		$icon_str .= "\t\t onMouseOver=\"window.status='Write a new message'; return true;\"\n";
		$icon_str .= "\t\t onMouseOut=\"window.status=''; return true;\">\n";
		$icon_str .= "\t\t<IMG SRC=\"$_wb'IconDir/$write\" BORDER=0 ALT='Write'></A>$br_tag\n";

	} else {
		$icon_str .=부분생략

		$icon_str .= "\t\t<A HREF=\"$_wb'WriteProgram?Db=$gDataBaseName&Mode=write&Block=$form{'Block'}\"\n";
		$icon_str .= "\t\t onMouseOver=\"window.status='Write a new message'; return true;\"\n";
		$icon_str .= "\t\t onMouseOut=\"window.status=''; return true;\">\n";
		$icon_str .= "\t\t<IMG SRC=\"$_wb'IconDir/$write\" BORDER=0 ALT='Write'></A>$br_tag\n";
	}

	if ($_wb'ViewInNewWindow && ! $form{'BackDepth'}) {
		$icon_str .=부분생략
	} else {
		if ($form{'BackDepth'} =~ /[0-9]+/) {
			$icon_str .=부분생략
		} else {
			$icon_str .=부분생략
	}

	$icon_str .= "$br_tag";

	return;
}
