my $sql="insert into WEPPRS (id,name,home,phone)values (?,?,?,?)";
my $sth =$dbh->prepare ($sql) ||die $dbh->errstr;

$id= $input{'id'};
$name= $input{'name'};
$home= $input{'home'};
$phone= $input{'phone'};
$sth->execute($id,$name,$home,$phone); 

print "rows1 rows inserted.\n";
$sth->finish;
$dbh->disconnect;

