<code>
use CGI; # for CGI
my $q = CGI->new(); # for CGI

my $sql=sprintf "insert into WEBPRS (id,name,home,phone) values ('?','?','?','?')";
my $name = $q->param('name');
my $id = $q->param('id'); 
my $home = $q->param('home'); 
my $phone = $q->param('phone');

my $sth =$dbh->prepare ("$sql") ||die $dbh->errstr;
$sth->execute($id,$name,$home,$phone); 

print "Content-type: text/html\n\n"; # for CGI
print "rows1 rows inserted.\n";

$sth->finish;
$dbh->commit;
$dbh->disconnect;
</code>

