>>>$sql = qq{
>>>           create table $t ( column1     varchar2(64),
>>>                             column2     varchar2(64),
>>>                             한국어컬럼1 varchar2(64),
>>>                             한국어컬럼2 varchar2(64)  )
>>>         };
>>>$sth = $dbh->prepare($sql) || die;
>>>$sth->execute;
>>>
