>>>$sql = qq{
>>>           insert into $t values ('column1 data',
>>>                                  'column2 data',
>>>                                  '한국어컬럼1 데이터',
>>>                                  '한국어컬럼2 데이터' )
>>>         };
>>>$sth = $dbh->prepare($sql) || die;
>>>$sth->execute;
