		@tmp=sort(@tmp);

		$l=0;
		for ($i=0 ; $i<@tmp ; $i++) {

			if ($tmp[$i] eq $tmp[$i+1]) {
				for ($k=$i+1 ; $k<@tmp ; $k++) {
					$tmp[$k]=$tmp[$k+1];
				}
			}

			$tmp_array[$l]=$tmp[$i];
			$l++;
		}

