encoding 모듈을 사용해 보세요.

<code>
use Tk;
use Tk::Button;

use encoding "euc-kr";

srand(time^$$);

@matrix = (	[0, 1, -1],	[-1, 0, 1],	[1, -1, 0] );
@word = ("가위", "바위", "보");

my $mw = MainWindow->new;

$label = $mw->Label(-text=>"아래 버튼들을 누질러주세요");
$label->pack;

$mw->Button(-text => "가위!", -command =>sub{game(0)}, -width=>30)->pack;
$mw->Button(-text => "바위!", -command =>sub{game(1)}, -width=>30)->pack;
$mw->Button(-text => "보!", -command =>sub{game(2)}, -width=>30)->pack;

sub game{
	my ($user_num) = @_;

	$com_num = int(rand(3));
	$win_code = $matrix[$user_num]->[$com_num];

	if ($win_code == -1) { $msg = "$word[$com_num]! 내가 졌다..."; }
	elsif ($win_code == 0) { $msg = "$word[$com_num]! 비겼다..."; }
	else { $msg = "$word[$com_num]! 내가 이겼다..."; }

	$label->configure(-text => $msg);		
}

MainLoop;
</code>

